import React from 'react';
import Slider from 'react-slick';

const HomePage = () => {
  const products = [
    { id: 1, image: "/product1.png", name: "Product 1" },
    { id: 2, image: "/product2.png", name: "Product 2" },
    { id: 3, image: "/product3.png", name: "Product 3" },
    { id: 4, image: "/product4.png", name: "Product 4" },
    { id: 5, image: "/product5.png", name: "Product 5" },
    { id: 6, image: "/product6.png", name: "Product 6" }
  ]

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    responsive: [
      {
        breakpoint: 768, // Mobile view
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  }

  return (
    <div className="container">
      <h2>Welcome to Wendy's Studio</h2>
      <p>Your cozy, modern hair, nail, and beauty care destination. We provide personalized services to make you feel pampered and refreshed.</p>
      <img src="/front-store.JPG" alt="Front Store View" className='homepageImage'/>
      <button onClick={() => window.location.href = '/services'}>View Our Services</button>
      <h3>Products</h3>
      <Slider {...sliderSettings}>
        {products.map(product => (
          <div key={product.id} className="product">
            <img src={product.image} alt={product.name} className="productImage" />
            <h4>{product.name}</h4>
          </div>
        ))}
      </Slider>
      <button onClick={() => window.location.href = '/products'}>View All Products</button>
    </div>
  );
};

export default HomePage;
