import React from 'react';
import Slider from 'react-slick';
import { products } from '../productsData';
const HomePage = () => {

    // Carousel settings for react-slick
    const settings = {
      dots: true,
      infinite: false,  // Disable infinite scroll to avoid issues
      speed: 500,
      slidesToShow: 3,   // Show 3 products
      slidesToScroll: 3, // Scroll 3 at a time
      responsive: [
        {
          breakpoint: 768,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1,
          },
        },
      ],
    };

  return (
    <div className="container">
      <h2>Welcome to Wendy's Studio</h2>
      <p>Your cozy, modern hair, nail, and beauty care destination. We provide personalized services to make you feel pampered and refreshed.</p>
      <img src="/front-store.JPG" alt="Front Store View" className='homepageImage'/>
      <button onClick={() => window.location.href = '/services'}>View Our Services</button>
      <h3>Products</h3>

      {/* Carousel */}
      <Slider {...settings}>
        {products.slice(0, 6).map((product, index) => (
          <div className="product-slide" key={index}>
            <img src={product.image} alt={product.name} className="product-image" />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
          </div>
        ))}
      </Slider>
      <button onClick={() => window.location.href = '/products'}>View All Products</button>
    </div>
  );
};

export default HomePage;
